def turns_to_prompt(turns: list[dict]) -> str:
    """
    Convert a multi-turn conversation into a single string prompt compatible with a base model, for instance:

    ```
    Q: Hello, how are you?
    A: I'm fine and you?

    Q: How much is 1+1?
    A:
    ```
    """
    prompt = ''
    for turn in turns:
        role = turn['role']
        msg = turn['message']
        if role == 'User':
            prompt += f'Q: {msg}\n'
        elif role == 'Chatbot':
            prompt += f'A: {msg}\n\n'
    if role == 'User':
        prompt += 'A:'
    return prompt.strip('\n')
